package ru.levap.cms.persistance.model;import java.util.Map;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.MapKey;import javax.persistence.OneToMany;import javax.validation.constraints.NotNull;import lombok.Getter;import lombok.Setter;@Entitypublic class Folder {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Getter    private Long id;    @NotNull    @Getter @Setter    private Boolean active;    @NotNull    @Getter @Setter    private String name;        @Getter @Setter    private String description;        @Getter @Setter    private String picture;    @NotNull    @Getter @Setter    private Long createDate;        @Getter @Setter    private Integer position;        @Getter @Setter    private Long updateDate;    @OneToMany(mappedBy = "folder")    @MapKey(name = "name")    @Getter @Setter    private Map<String, FolderParameter> parameters;        @ManyToOne    @JoinColumn(name = "parentId")    @Getter @Setter    private Folder parent;        @ManyToOne    @JoinColumn(name = "frontImageId")    @Getter @Setter    private Folder frontImage;}